INSERT INTO `articles` VALUES (38, '乳化油脂对母猪可优化解决的问题', '<table cellspacing=\"0\" cellpadding=\"0\">\n<tbody>\n<tr>\n<td id=\"postmessage_3381550\" class=\"t_f\">\n<table class=\"t_table\" cellspacing=\"0\">\n<tbody>\n<tr>\n<td><span style=\"color: #333333;\"><span style=\"font-family: &amp;quot;\">1、组成细胞膜</span></span><br><span style=\"color: #333333;\"><span style=\"font-family: &amp;quot;\">多不饱和脂肪酸是细胞膜磷脂的重要组成部分，是决定细胞膜功能的重要因素。产品可促进乳腺腺泡和细小乳导管的分泌上皮细胞的发育，加强膜流动性，从而促进膜上物质转运。</span></span><br><br><br><span style=\"color: #333333;\"><span style=\"font-family: &amp;quot;\">2、调节泌乳激素</span></span><br><span style=\"color: #333333;\"><span style=\"font-family: &amp;quot;\">产品中的多不饱和脂肪酸在体内能够合成雌激素、孕酮等类固醇激素，从而调节母猪的乳腺发育及泌乳机能，明显提高母猪泌乳量。</span></span><br><br><br><span style=\"color: #333333;\"><span style=\"font-family: &amp;quot;\">3、提高奶水质量</span></span><br><span style=\"color: #333333;\"><span style=\"font-family: &amp;quot;\">提供母乳中所需的脂肪酸模式，提升母乳质量，促使母猪顺畅泌乳，减少乳房炎的发生几率。</span></span><br><span style=\"color: #333333;\"><span style=\"font-family: &amp;quot;\">4、提高抗病力</span></span><br><span style=\"color: #333333;\"><span style=\"font-family: &amp;quot;\">产品中含有较为丰富的不饱和脂肪酸和必需脂肪酸，提供机体免疫和肠道修复的组分。</span></span><br><span style=\"color: #333333;\"><span style=\"font-family: &amp;quot;\">5、提升仔猪质量（怀孕母猪）</span></span><br><span style=\"color: #333333;\"><span style=\"font-family: &amp;quot;\">多不饱和脂肪酸可以增加红细胞的柔韧性，增加血液的流速和红细胞的携氧能力，促进穿过胎膜的血液和营养转运，供给胎儿更多的营养，提高仔猪的成活率和初生重。</span></span><br><span style=\"color: #333333;\"><span style=\"font-family: &amp;quot;\">6、促进发情</span></span><br><span style=\"color: #333333;\"><span style=\"font-family: &amp;quot;\">产品中的必需脂肪酸能够完善母猪生殖机能，保证生殖健康，从而促使母猪正常发情。</span></span><br><span style=\"color: #333333;\"><span style=\"font-family: &amp;quot;\">7、提高产乳量与乳营养</span></span><br><span style=\"color: #333333;\"><span style=\"font-family: &amp;quot;\">富含高消化率水溶性脂肪，提供哺乳母猪能量快速补充，提高产乳量与乳营养，提高断奶仔猪成活率增强哺乳母猪体质。</span></span><br><span style=\"font-family: 微软雅黑;\">要是有兴趣，直接联系vx：J13750353099</span><img src=\"http://www.xumuren.com/static/image/smiley/default/handshake.gif\" alt=\"\" border=\"0\"></td>\n</tr>\n</tbody>\n</table>\n</td>\n</tr>\n</tbody>\n</table>\n<p>&nbsp;</p>', '匿名', '2025-04-11 10:41:08', '2025-04-11 10:41:08', 15);
INSERT INTO `articles` VALUES (39, '后备母猪的饲养管理与保健', '<p><span style=\"color: #000000;\">后备母猪的饲养管理是母猪饲养的关键阶段，这一阶段的饲养管理阶段为生产母猪打下良好的基础，对成年后的生产性能、使用年限都有积极的影响。</span><br><span style=\"color: #000000;\">你可以根据你猪场后备母猪的情况设计合适的日粮，不同品种的猪具有不同的营养标准。还要考虑后备母猪的环境条件，因为环境条件会影响猪每日营养的摄入量和采食量。</span><br><span style=\"color: #000000;\">后备母猪每天所需要的消化能摄入量不能少于30MJ；其次钙、磷等维生素的摄入量也不能少，要保证日粮的营养水平，还要注意控制后备母猪的背膘厚度，既不能过多也不能过少，过少的话不利于提高母猪的繁殖能力，过多的话可能会给母猪的生产造成诸多的不便。后备母猪初配时背膘处于16~22mm的厚度时较为适合。</span><br><span style=\"color: #000000;\">当然也要提高环境卫生，应保持圈舍清洁、干燥、饮水应清洁卫生。饲养人员进入猪舍时也要做好自身的卫生清洁，穿好无菌服，同时根据季节的变化做好通风工作，防止口蹄疫和猪瘟等疾病的发生，夏季应定期做好对猪舍内外环境的驱蚊蝇消毒工作，同时做好温度稳定，避免温度应激。</span></p>', '卡德加', '2025-04-18 14:37:14', '2025-04-18 14:37:14', 3);

-- ----------------------------
-- Table structure for conversation
-- ----------------------------
DROP TABLE IF EXISTS `conversation`;
CREATE TABLE `conversation`  (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `session_id` bigint NOT NULL COMMENT '会话ID',
  `user_id` int NOT NULL COMMENT '用户ID',
  `user_input` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '用户输入',
  `ai_response` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT 'AI回复',
  `conversation_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '对话时间',
  `model_name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT 'AI模型名称',
  `response_time` decimal(10, 2) NULL DEFAULT NULL COMMENT 'AI响应时间（秒）',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 16 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '对话' ROW_FORMAT = DYNAMIC;

