-- ----------------------------
-- Table structure for illness
-- ----------------------------
DROP TABLE IF EXISTS `illness`;
CREATE TABLE `illness`  (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '疾病id',
  `kind_id` int NULL DEFAULT NULL COMMENT '疾病分类ID',
  `illness_name` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '疾病名字',
  `include_reason` mediumtext CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '诱发因素',
  `illness_symptom` mediumtext CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '疾病症状',
  `special_symptom` mediumtext CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '特殊症状',
  `create_time` datetime NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 121227 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '疾病' ROW_FORMAT = DYNAMIC;

-- ----------------------------
-- Records of illness
-- ----------------------------
INSERT INTO `illness` VALUES (121213, 18, '猪肺炎1', '<h3 class=\"header-jfw95c auto-hide-last-sibling-br\" style=\"text-align: center;\"><strong>一、诱发因素</strong></h3>\n<ol class=\"auto-hide-last-sibling-br\">\n<li style=\"text-align: center;\">\n<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\"><strong>病原体感染</strong></div>\n<ul class=\"auto-hide-last-sibling-br\">\n<li><strong>细菌性</strong>：如猪肺炎支原体（支原体肺炎）、多杀性巴氏杆菌（巴氏杆菌病）、胸膜肺炎放线杆菌（传染性胸膜肺炎）、副猪嗜血杆菌等。</li>\n<li><strong>病毒性</strong>：如猪繁殖与呼吸综合征病毒（蓝耳病）、猪流感病毒、伪狂犬病病毒、猪圆环病毒（继发感染）等。</li>\n<li><strong>其他病原</strong>：如衣原体、霉菌（如曲霉菌）、寄生虫（如肺丝虫）等。</li>\n</ul>\n</li>\n<li>\n<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\" style=\"text-align: center;\"><strong>环境因素</strong></div>\n<ul class=\"auto-hide-last-sibling-br\">\n<li style=\"text-align: center;\">猪舍通风不良、空气浑浊（氨气浓度过高）、潮湿或干燥。</li>\n<li style=\"text-align: center;\">养殖密度过高，导致应激和交叉感染风险增加。</li>\n<li style=\"text-align: center;\">温度骤变、昼夜温差大或冬季防寒不足，诱发呼吸道黏膜屏障受损。</li>\n<li style=\"text-align: center;\">饲料粉尘、霉菌毒素（如黄曲霉毒素）刺激呼吸道。</li>\n</ul>\n</li>\n</ol>', '<h3 class=\"header-jfw95c auto-hide-last-sibling-br\" style=\"text-align: center;\"><strong>二、疾病症状（典型共性症状）</strong></h3>\n<ol class=\"auto-hide-last-sibling-br\">\n<li style=\"text-align: center;\">\n<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\"><strong>全身症状</strong></div>\n<ul class=\"auto-hide-last-sibling-br\">\n<li>体温升高（39.5℃以上，细菌性肺炎常达 40-41℃）。</li>\n<li>精神沉郁，食欲减退或废绝，体重增长缓慢。</li>\n<li>仔猪可能出现被毛粗乱、消瘦、扎堆取暖。</li>\n</ul>\n</li>\n<li style=\"text-align: center;\">\n<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\"><strong>呼吸道症状</strong></div>\n<ul class=\"auto-hide-last-sibling-br\">\n<li><strong>咳嗽</strong>：频繁干咳（如支原体肺炎）或湿咳（细菌性肺炎伴分泌物）。</li>\n<li><strong>呼吸困难</strong>：呼吸频率加快（腹式呼吸），严重时张口喘气、鼻翼扇动。</li>\n<li><strong>鼻腔分泌物</strong>：浆液性（初期）、黏液性或脓性（细菌性感染），部分病例带血（如胸膜肺炎）。</li>\n</ul>\n</li>\n<li>\n<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\" style=\"text-align: center;\"><strong>肺部听诊</strong></div>\n<ul class=\"auto-hide-last-sibling-br\">\n<li style=\"text-align: center;\">可闻及湿性啰音、干啰音或胸膜摩擦音（胸膜炎时）。</li>\n</ul>\n</li>\n</ol>', '<h3 class=\"header-jfw95c auto-hide-last-sibling-br\" style=\"text-align: center;\"><strong>三、特殊症状（不同病原 / 类型的特征表现）</strong></h3>\n<ol class=\"auto-hide-last-sibling-br\">\n<li style=\"text-align: center;\">\n<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\"><strong>猪支原体肺炎（地方流行性肺炎）</strong></div>\n<ul class=\"auto-hide-last-sibling-br\">\n<li><strong>慢性干咳</strong>：清晨、运动后或气温骤变时咳嗽加剧，呈连续性或痉挛性。</li>\n<li><strong>气喘</strong>：呼吸频率可达 60-80 次 / 分钟，呈明显的腹式呼吸，无明显发热。</li>\n<li><strong>生长缓慢</strong>：病程长，影响饲料转化率，俗称 &ldquo;气喘病&rdquo;。</li>\n</ul>\n</li>\n<li style=\"text-align: center;\">\n<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\"><strong>传染性胸膜肺炎</strong></div>\n<ul class=\"auto-hide-last-sibling-br\">\n<li><strong>急性死亡</strong>：育肥猪多见，死前可能无明显症状，或表现张口呼吸、口鼻流带血泡沫。</li>\n<li><strong>剖检特征</strong>：肺脏与胸膜粘连，胸腔有血色积液，肺实质可见出血性坏死。</li>\n</ul>\n</li>\n<li style=\"text-align: center;\">\n<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\"><strong>蓝耳病继发肺炎</strong></div>\n<ul class=\"auto-hide-last-sibling-br\">\n<li><strong>耳部发绀</strong>：耳尖、边缘或腹部皮肤出现蓝紫色斑块。</li>\n<li><strong>繁殖障碍</strong>：母猪流产、死胎；仔猪呼吸困难、存活率低。</li>\n</ul>\n</li>\n<li style=\"text-align: center;\">\n<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\"><strong>巴氏杆菌病（肺疫）</strong></div>\n<ul class=\"auto-hide-last-sibling-br\">\n<li><strong>咽喉部肿胀</strong>：颈部皮下水肿，呼吸困难伴犬坐姿势。</li>\n<li><strong>败血症症状</strong>：皮肤出现紫斑，死亡率较高。</li>\n</ul>\n</li>\n<li>\n<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\" style=\"text-align: center;\"><strong>霉菌性肺炎（如曲霉菌病）</strong></div>\n<ul class=\"auto-hide-last-sibling-br\">\n<li style=\"text-align: center;\"><strong>体温正常或微升</strong>：咳嗽较轻，呼吸音粗粝，伴鼻黏膜溃疡或结节。</li>\n<li style=\"text-align: center;\"><strong>剖检可见</strong>：肺脏有灰白色霉菌结节或菌丝团。</li>\n<li>\n<h3 class=\"header-jfw95c auto-hide-last-sibling-br\" style=\"text-align: center;\"><strong>四、注意事项</strong></h3>\n<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\" style=\"text-align: center;\">猪肺炎常呈混合感染（如 &ldquo;蓝耳病 + 支原体 + 巴氏杆菌&rdquo;），症状复杂，需结合流行病学、剖检病变及实验室检测（病原分离、PCR）确诊。预防关键在于改善养殖环境、做好疫苗免疫（如支原体、胸膜肺炎疫苗）及减少应激，治疗需根据病原选择敏感抗生素（如泰妙菌素、氟苯尼考）或抗病毒药物（配合干扰素等）。</div>\n</li>\n</ul>\n</li>\n</ol>', '2025-02-25 23:45:49', '2025-02-25 23:45:49');
INSERT INTO `illness` VALUES (121214, 18, '猪肺炎2', '饲料霉变', '食欲减退、消瘦', '肺部啰音', '2025-02-25 23:45:49', '2025-02-25 23:45:49');
INSERT INTO `illness` VALUES (121215, 18, '猪肺炎3', '病毒感染', '精神萎靡、咳嗽', '肺部炎症', '2025-02-25 23:45:49', '2025-02-25 23:45:49');
INSERT INTO `illness` VALUES (121216, 19, '水肿病1', '<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\">生猪水肿病（猪大肠杆菌性水肿病）的发生与多种应激及饲养管理因素密切相关，核心诱因包括：</div>\n<ul class=\"auto-hide-last-sibling-br\">\n<li><strong>肠道菌群失衡</strong>：断奶前后（2-4 周龄仔猪常见），饲料突然更换、蛋白质含量过高（如豆粕比例过大）或缺乏维生素（如 VE、硒），导致肠道溶血性大肠杆菌（如 O139、O141 血清型）异常增殖并释放毒素。</li>\n<li><strong>应激反应</strong>：断奶、环境突变（温度骤变、高密度养殖）、运输等应激降低仔猪免疫力，破坏肠道黏膜屏障，为病原菌侵袭创造条件。</li>\n<li><strong>卫生条件差</strong>：圈舍潮湿脏乱、消毒不彻底，增加仔猪接触病原菌的风险，尤其在通风不良的环境中发病率更高。</li>\n</ul>', '<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\" style=\"text-align: center;\">发病急骤，典型症状分为&nbsp;<strong>全身表现</strong>&nbsp;与&nbsp;<strong>神经症状</strong>，病程短（多在 48 小时内死亡）：</div>\n<ol class=\"auto-hide-last-sibling-br\">\n<li style=\"text-align: center;\"><strong>全身症状</strong>：\n<ul class=\"auto-hide-last-sibling-br\">\n<li>初期精神沉郁，食欲废绝，体温正常或微升（39.5-40℃），心跳加快，呼吸急促。</li>\n<li>粪便异常：初期轻微腹泻，随后转为便秘，部分仔猪呕吐或口吐白沫。</li>\n</ul>\n</li>\n<li style=\"text-align: center;\"><strong>神经症状</strong>：\n<ul class=\"auto-hide-last-sibling-br\">\n<li style=\"text-align: center;\">运动失调：步态不稳，转圈、冲撞墙壁，后躯麻痹或四肢呈 &ldquo;游泳状&rdquo; 划动，触诊肌肉震颤、叫声嘶哑。</li>\n<li style=\"text-align: center;\">感觉过敏：对声音、触摸等刺激反应强烈，惊叫不止，最终因呼吸衰竭或心力衰竭死亡。</li>\n</ul>\n</li>\n</ol>', '<ul class=\"auto-hide-last-sibling-br\">\n<li style=\"text-align: center;\"><strong>体表水肿</strong>：\n<ul class=\"auto-hide-last-sibling-br\">\n<li><strong>头部水肿</strong>：眼睑、颜面、下颌及颈部皮下水肿，严重时头部肿大（俗称 &ldquo;大头病&rdquo;），指压皮肤出现凹陷性水肿。</li>\n<li><strong>肢体水肿</strong>：腹部、四肢内侧皮肤可见弥漫性水肿，触感松软有波动感。</li>\n</ul>\n</li>\n<li style=\"text-align: center;\"><strong>剖检特征</strong>：\n<ul class=\"auto-hide-last-sibling-br\">\n<li style=\"text-align: center;\"><strong>消化道水肿</strong>：胃壁（胃大弯、贲门部）黏膜下层呈胶冻样水肿，肠系膜淋巴结肿大、出血，肠壁水肿增厚。</li>\n<li style=\"text-align: center;\"><strong>体腔积液</strong>：心包腔、胸腹腔可见淡黄色透明或胶冻状积液，肺脏、肝脏表面偶见出血点。</li>\n</ul>\n</li>\n</ul>', '2025-02-25 23:45:49', '2025-02-25 23:45:49');
