INSERT INTO `illness` VALUES (121224, 49, '嗜血杆菌病3', '免疫力低下', '食欲减退、消瘦', '全身症状', '2025-02-25 23:45:49', '2025-02-25 23:45:49');
INSERT INTO `illness` VALUES (121226, 18, '测试测试', '<ul>\n<li class=\"header-jfw95c auto-hide-last-sibling-br\" style=\"text-align: center;\">感冒的病因分析</li>\n<li class=\"header-jfw95c auto-hide-last-sibling-br\" style=\"text-align: center;\">一、主要致病病毒</li>\n<li class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\" style=\"text-align: center;\">感冒通常由<strong>微小核糖核酸病毒</strong>引发，具体包括：</li>\n<li style=\"text-align: center;\">&nbsp;</li>\n</ul>\n<ul class=\"auto-hide-last-sibling-br\" style=\"text-align: center;\">\n<li><strong>鼻病毒</strong>（最常见）</li>\n<li><strong>埃可病毒</strong>（肠道病毒属）</li>\n<li><strong>柯萨奇病毒</strong>（肠道病毒属）</li>\n<li>少量由以下病毒引起：\n<ul class=\"auto-hide-last-sibling-br\">\n<li>冠状病毒</li>\n<li>流感病毒</li>\n<li>副流感病毒</li>\n<li>腺病毒</li>\n<li>呼吸道合胞病毒</li>\n<li>副粘病毒（Paramyxovirus）</li>\n</ul>\n</li>\n</ul>\n<ul style=\"text-align: center;\">\n<li style=\"text-align: center;\">&nbsp;</li>\n<li class=\"auto-hide-last-sibling-br\">\n<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\">提示：可引起感冒的病毒超过 100 种，多数情况下，感冒痊愈前难以精确判断具体致病病毒。</div>\n</li>\n<li class=\"header-jfw95c auto-hide-last-sibling-br\" style=\"text-align: center;\">二、诱发条件</li>\n<li class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\" style=\"text-align: center;\">感冒的发生与机体抵抗力下降密切相关，常见诱发因素包括：</li>\n<li style=\"text-align: center;\">&nbsp;</li>\n</ul>\n<ul class=\"auto-hide-last-sibling-br\">\n<li style=\"text-align: center;\"><strong>受凉、淋雨或过度疲劳</strong></li>\n<li style=\"text-align: center;\"><strong>长时间暴露于剧烈寒冷环境</strong>（非直接病因，但会导致免疫系统功能减弱，增加病毒易感性）</li>\n</ul>', '<h3 class=\"header-jfw95c auto-hide-last-sibling-br\" style=\"text-align: center;\">三、感冒的流行病学特征</h3>\n<h4 class=\"header-jfw95c auto-hide-last-sibling-br\" style=\"text-align: center;\">1. 发病规律</h4>\n<ul class=\"auto-hide-last-sibling-br\" style=\"text-align: center;\">\n<li><strong>季节分布</strong>：<br class=\"container-eLPxqH wrapper-wt12I4 undefined\">普通感冒多发于初冬，但春夏季亦可发生，不同季节致病病毒存在差异（以冠状病毒、鼻病毒为主要病原体）。</li>\n<li><strong>传播特点</strong>：\n<ul class=\"auto-hide-last-sibling-br\">\n<li>散发性病例，不引起流行，易合并细菌感染。</li>\n<li>病毒通过呼吸道分泌物传播，当机体抵抗力下降时（如受凉、疲劳、营养不良等）易诱发感染。</li>\n</ul>\n</li>\n</ul>\n<h3 class=\"header-jfw95c auto-hide-last-sibling-br\" style=\"text-align: center;\">四、感冒的临床症状与分型</h3>\n<h4 class=\"header-jfw95c auto-hide-last-sibling-br\" style=\"text-align: center;\">（一）典型症状（潜伏期 24~72 小时）</h4>\n<ol class=\"auto-hide-last-sibling-br\" style=\"text-align: center;\">\n<li><strong>全身表现</strong>：\n<ul class=\"auto-hide-last-sibling-br\">\n<li>突然起病，伴鼻 / 喉部灼热感、打喷嚏、流涕、全身不适，<strong>通常不发热</strong>（鼻病毒 / 冠状病毒感染特征）。</li>\n<li>早期多有咽炎，喉炎、气管炎表现因人而异。</li>\n</ul>\n</li>\n<li><strong>局部表现</strong>：\n<ul class=\"auto-hide-last-sibling-br\">\n<li>鼻腔分泌物：初期为水样，后转为粘液样 / 脓性（<strong>非细菌感染特异性表现</strong>）。</li>\n<li>咳嗽：多不剧烈，但可持续 2 周；慢性呼吸道疾病患者易出现支气管炎加剧。</li>\n</ul>\n</li>\n<li><strong>病程</strong>：无并发症时，4~10 天内症状消失。</li>\n</ol>\n<h4 class=\"header-jfw95c auto-hide-last-sibling-br\" style=\"text-align: center;\">（二）分型特征</h4>\n<h5 class=\"header-jfw95c auto-hide-last-sibling-br\" style=\"text-align: center;\">▶ 上呼吸道感冒</h5>\n<ul class=\"auto-hide-last-sibling-br\" style=\"text-align: center;\">\n<li><strong>炎症范围</strong>：鼻、鼻窦、喉、咽，常累及气管、支气管。</li>\n<li><strong>症状特点</strong>：\n<ul class=\"auto-hide-last-sibling-br\">\n<li>全身症状轻：轻度畏寒、头痛，<strong>不发热或低热</strong>。</li>\n<li>局部症状：鼻塞、流涕（初期水样&rarr;后期粘液 / 脓性）、咽痛、咽干、轻咳（白痰），病程 3~5 日。</li>\n</ul>\n</li>\n<li><strong>实验室检查</strong>：白细胞数量正常或增多。</li>\n</ul>\n<h5 class=\"header-jfw95c auto-hide-last-sibling-br\" style=\"text-align: center;\">▶ 胃肠型感冒</h5>\n<ul class=\"auto-hide-last-sibling-br\" style=\"text-align: center;\">\n<li><strong>核心特征</strong>：\n<ul class=\"auto-hide-last-sibling-br\">\n<li><strong>上呼吸道症状轻微或缺失</strong>，以消化道症状为主，易误诊为肠胃炎。</li>\n</ul>\n</li>\n<li><strong>典型表现</strong>：\n<ul class=\"auto-hide-last-sibling-br\">\n<li>胃肠症状：上腹不适、反酸、恶心、呕吐、腹痛、水样腹泻（无脓血）。</li>\n<li>全身表现：畏寒、发热、头痛。</li>\n</ul>\n</li>\n<li><strong>实验室检查</strong>：中性粒细胞增多，部分伴上呼吸道感染。</li>\n</ul>\n<h3 class=\"header-jfw95c auto-hide-last-sibling-br\" style=\"text-align: center;\">五、感冒与相似疾病的鉴别诊断</h3>\n<div class=\"auto-hide-last-sibling-br table-container-zGnRBL\" style=\"text-align: center;\">\n<table>\n<thead>\n<tr>\n<th><strong>疾病</strong></th>\n<th><strong>关键鉴别点</strong></th>\n</tr>\n</thead>\n<tbody>\n<tr>\n<td><strong>百日咳</strong></td>\n<td>- 潜伏期 7~10 天后出现<strong>阵发性痉咳</strong>，连续数十声后伴鹭鸶鸣样吸气吼声，病程可达 3 个月。</td>\n</tr>\n<tr>\n<td><strong>猩红热</strong></td>\n<td>- 咽部红肿疼痛剧烈，1 日内出现典型皮疹，舌呈 &ldquo;杨梅舌&rdquo;，与感冒症状差异显著。</td>\n</tr>\n<tr>\n<td><strong>小儿脊髓灰质炎</strong></td>\n<td>-&nbsp;<strong>双相热型</strong>（体温正常 1~6 天后再次发热），随后出现肌肉疼痛、肢体瘫痪等神经症状。</td>\n</tr>\n</tbody>\n</table>\n</div>\n<h3 class=\"header-jfw95c auto-hide-last-sibling-br\" style=\"text-align: center;\">六、感冒常见并发症（需密切观察）</h3>\n<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\" style=\"text-align: center;\">⚠️ 出现以下症状需及时就医：</div>\n<h2 style=\"text-align: center;\">&nbsp;</h2>\n<ul class=\"auto-hide-last-sibling-br\">\n<li style=\"text-align: center;\"><strong>中耳炎</strong>：持续高烧（&gt;3 天）、耳朵疼痛、婴幼儿烦躁 / 抓耳。</li>\n<li style=\"text-align: center;\"><strong>鼻窦炎</strong>：流涕超 10 天未缓解，伴黄绿色脓涕、严重鼻塞、头痛。</li>\n<li style=\"text-align: center;\"><strong>肺炎</strong>：高烧不退、咳嗽加剧、呼吸急促、食欲锐减。</li>\n<li style=\"text-align: center;\"><strong>脑膜炎</strong>：颈部僵硬、剧烈头痛、呕吐、畏光、意识模糊。</li>\n</ul>', '<h3 class=\"header-jfw95c auto-hide-last-sibling-br\" style=\"text-align: center;\">五、感冒的治疗与预防</h3>\n<h4 class=\"header-jfw95c auto-hide-last-sibling-br\" style=\"text-align: center;\">（一）治疗原则</h4>\n<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\" style=\"text-align: center;\">目前尚无特效抗病毒药物，以<strong>支持疗法</strong>为主，依靠免疫系统清除病毒，辅以对症治疗缓解症状：</div>\n<h2 style=\"text-align: center;\">&nbsp;</h2>\n<ul class=\"auto-hide-last-sibling-br\" style=\"text-align: center;\">\n<li><strong>基础护理</strong>：充分休息、大量饮水、清淡饮食（关键！可加速康复）。</li>\n<li><strong>对症治疗</strong>：使用药物减轻鼻塞、发热、头痛等症状，避免过度用药。</li>\n</ul>\n<h3 class=\"header-jfw95c auto-hide-last-sibling-br\" style=\"text-align: center;\">（二）药物治疗分类</h3>\n<h4 class=\"header-jfw95c auto-hide-last-sibling-br\" style=\"text-align: center;\">▶ 1. 解热镇痛抗炎剂（非类固醇类）</h4>\n<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\" style=\"text-align: center;\">主要缓解发热、头痛、肌肉酸痛，兼具消炎作用：</div>\n<h2 style=\"text-align: center;\">&nbsp;</h2>\n<ul class=\"auto-hide-last-sibling-br\" style=\"text-align: center;\">\n<li><strong>阿司匹林</strong></li>\n<li><strong>扑热息痛（对乙酰氨基酚）</strong></li>\n<li><strong>布洛芬（Ibuprofen）</strong></li>\n</ul>\n<h4 class=\"header-jfw95c auto-hide-last-sibling-br\" style=\"text-align: center;\">▶ 2. 抗生素</h4>\n<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\" style=\"text-align: center;\">⚠️&nbsp;<strong>使用原则</strong>：</div>\n<h2 style=\"text-align: center;\">&nbsp;</h2>\n<ul class=\"auto-hide-last-sibling-br\" style=\"text-align: center;\">\n<li>仅用于<strong>继发细菌感染</strong>（如脓痰、持续高热、白细胞升高）。</li>\n<li>病毒感染时滥用易导致耐药性，无细菌感染证据时禁用。</li>\n</ul>\n<h4 class=\"header-jfw95c auto-hide-last-sibling-br\" style=\"text-align: center;\">▶ 3. 抗敏与通鼻药物</h4>\n<ul class=\"auto-hide-last-sibling-br\" style=\"text-align: center;\">\n<li><strong>抗组胺药</strong>：\n<ul class=\"auto-hide-last-sibling-br\">\n<li>第一代：扑尔敏（可能引起嗜睡）</li>\n<li>第二代：氯雷他定（无嗜睡副作用）</li>\n<li>第三代：地氯雷他定（高效低副作用）</li>\n</ul>\n</li>\n<li><strong>减充血剂</strong>：缓解鼻塞（如伪麻黄碱），需按疗程使用避免反跳性充血。</li>\n</ul>\n<h4 class=\"header-jfw95c auto-hide-last-sibling-br\" style=\"text-align: center;\">▶ 4. 抗病毒药物</h4>\n<ul class=\"auto-hide-last-sibling-br\" style=\"text-align: center;\">\n<li><strong>适用人群</strong>：\n<ul class=\"auto-hide-last-sibling-br\">\n<li>免疫缺陷患者（如 HIV 感染者、肿瘤患者）可早期使用。</li>\n<li>普通人群无发热、免疫正常且发病超 2 天者，一般无需使用（滥用易致耐药）。</li>\n</ul>\n</li>\n<li><strong>常用药物</strong>：利巴韦林、奥司他韦（广谱抗病毒，对感冒疗效有限，需遵医嘱）。</li>\n</ul>\n<h4 class=\"header-jfw95c auto-hide-last-sibling-br\" style=\"text-align: center;\">▶ 5. 补充剂</h4>\n<ul class=\"auto-hide-last-sibling-br\" style=\"text-align: center;\">\n<li><strong>维生素 C</strong>：可增强免疫力，常规剂量（100-200mg / 日）有助于缩短病程，过量无明确效果。</li>\n</ul>\n<h3 class=\"header-jfw95c auto-hide-last-sibling-br\" style=\"text-align: center;\">（三）预防措施</h3>\n<h4 class=\"header-jfw95c auto-hide-last-sibling-br\" style=\"text-align: center;\">▶ 核心策略</h4>\n<ol class=\"auto-hide-last-sibling-br\" style=\"text-align: center;\">\n<li><strong>增强体质</strong>：规律锻炼、均衡饮食、充足睡眠，提升免疫力。</li>\n<li><strong>避免诱因</strong>：防止受凉、过度劳累，减少免疫力下降风险。</li>\n<li><strong>阻断传播</strong>：\n<ul class=\"auto-hide-last-sibling-br\">\n<li>勤洗手（尤其接触公共物品后），避免用手触摸眼口鼻。</li>\n<li>污染环境定期消毒，患者分泌物需密封处理。</li>\n</ul>\n</li>\n</ol>\n<h3 class=\"header-jfw95c auto-hide-last-sibling-br\" style=\"text-align: center;\">（四）中医对感冒的认识与治疗</h3>\n<h4 class=\"header-jfw95c auto-hide-last-sibling-br\" style=\"text-align: center;\">▶ 中医病名与病因</h4>\n<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\" style=\"text-align: center;\">感冒属外感病，因风寒暑湿燥火六淫邪气侵袭所致，分轻重、寒热：</div>\n<h2 style=\"text-align: center;\">&nbsp;</h2>\n<ul class=\"auto-hide-last-sibling-br\" style=\"text-align: center;\">\n<li><strong>经典论述</strong>（《丹溪心法》《医理真传》）：<br class=\"container-eLPxqH wrapper-wt12I4 undefined\">&ldquo;感冒为病，亦有风寒二证，即是伤寒外证初起之轻者&rdquo;&mdash;&mdash;《证治要诀》<br class=\"container-eLPxqH wrapper-wt12I4 undefined\">强调外感病邪由外而入，需辨明风寒、风热、时行（流行）等证型。</li>\n</ul>\n<h4 class=\"header-jfw95c auto-hide-last-sibling-br\" style=\"text-align: center;\">▶ 辨证施治</h4>\n<div class=\"auto-hide-last-sibling-br table-container-zGnRBL\" style=\"text-align: center;\">&nbsp;&nbsp;\n<table style=\"width: 98.2554%; height: 187.4px;\">\n<thead>\n<tr style=\"height: 35px;\">\n<th style=\"width: 19.0438%;\"><strong>证型</strong></th>\n<th style=\"width: 21.1598%;\"><strong>治法</strong></th>\n<th style=\"width: 26.4498%;\"><strong>代表方剂</strong></th>\n<th style=\"width: 33.3267%;\"><strong>症状特征</strong></th>\n</tr>\n</thead>\n<tbody>\n<tr style=\"height: 33.2px;\">\n<td style=\"width: 19.0438%;\">风寒感冒</td>\n<td style=\"width: 21.1598%;\">辛温解表</td>\n<td style=\"width: 26.4498%;\">葱豉汤、荆防败毒散</td>\n<td style=\"width: 33.3267%;\">恶寒重、无汗、流清涕、苔薄白</td>\n</tr>\n<tr style=\"height: 33.2px;\">\n<td style=\"width: 19.0438%;\">风热感冒</td>\n<td style=\"width: 21.1598%;\">辛凉解表</td>\n<td style=\"width: 26.4498%;\">银翘散、桑菊饮</td>\n<td style=\"width: 33.3267%;\">发热重、咽痛、黄涕、舌尖红</td>\n</tr>\n<tr style=\"height: 33.2px;\">\n<td style=\"width: 19.0438%;\">虚人感冒</td>\n<td style=\"width: 21.1598%;\">扶正解表</td>\n<td style=\"width: 26.4498%;\">参苏饮</td>\n<td style=\"width: 33.3267%;\">恶寒发热 + 气短、乏力等虚象</td>\n</tr>\n<tr style=\"height: 52.8px;\">\n<td style=\"width: 19.0438%;\">时行感冒（流感）</td>\n<td style=\"width: 21.1598%;\">清热解毒 / 表里双解</td>\n<td style=\"width: 26.4498%;\">清瘟败毒散、柴葛解肌汤</td>\n<td style=\"width: 33.3267%; text-align: center;\">病情较重，伴高热、全身酸痛</td>\n</tr>\n</tbody>\n</table>\n</div>\n<h4 class=\"header-jfw95c auto-hide-last-sibling-br\" style=\"text-align: center;\">▶ 经典文献引用</h4>\n<ul class=\"auto-hide-last-sibling-br\">\n<li style=\"text-align: center;\">《七松岩集》警示：&ldquo;感冒轻浅，忽略不治，兼饮食不节，以致轻病变重&rdquo;，强调早期干预的重要性。</li>\n<li style=\"text-align: center;\">《医宗必读》记载消风散、川芎茶调散等方，用于治疗头痛、鼻塞声重等症，体现 &ldquo;辨证选方&rdquo; 原则。</li>\n</ul>', '2025-04-19 12:26:22', '2025-04-19 12:26:22');

-- ----------------------------
-- Table structure for illness_kind
-- ----------------------------
DROP TABLE IF EXISTS `illness_kind`;
CREATE TABLE `illness_kind`  (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '分类名称',
  `info` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '描述',
  `create_time` datetime NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 52 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '疾病种类' ROW_FORMAT = DYNAMIC;

-- ----------------------------
-- Records of illness_kind
-- ----------------------------
INSERT INTO `illness_kind` VALUES (18, '猪肺炎', '一种影响猪呼吸系统的疾病', '2025-02-25 23:42:16', '2025-02-25 23:42:16');
