-- ----------------------------
-- Table structure for knowledge_file
-- ----------------------------
DROP TABLE IF EXISTS `knowledge_file`;
CREATE TABLE `knowledge_file`  (
  `id` bigint UNSIGNED NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `file_name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '原始文件名（含后缀）',
  `file_path` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '相对路径：相对于 resources/knowledge/',
  `file_size` bigint NOT NULL COMMENT '单位：字节',
  `file_md5` char(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '文件内容 MD5',
  `file_type` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'MIME-Type，如 application/pdf',
  `create_by` int NULL DEFAULT NULL COMMENT '上传人',
  `create_time` datetime NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '修改时间',
  `remark` varchar(1000) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '备注',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `idx_md5`(`file_md5` ASC) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 4 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci COMMENT = 'RAG 知识库文件表' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of knowledge_file
-- ----------------------------

-- ----------------------------
-- Table structure for medicine
-- ----------------------------
DROP TABLE IF EXISTS `medicine`;
CREATE TABLE `medicine`  (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '药品主键ID',
  `medicine_name` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '药的名字',
  `keyword` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '关键字搜索',
  `medicine_effect` mediumtext CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '药的功效',
  `medicine_brand` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '药的品牌',
  `interaction` mediumtext CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '药的相互作用',
  `taboo` mediumtext CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '禁忌',
  `us_age` mediumtext CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '用法用量',
  `medicine_type` int NULL DEFAULT NULL COMMENT '药的类型，0西药，1中药，2中成药',
  `img_path` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '相关图片路径',
  `medicine_price` decimal(10, 2) NULL DEFAULT NULL COMMENT '药的价格',
  `create_time` datetime NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 50 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '药品' ROW_FORMAT = DYNAMIC;

-- ----------------------------
-- Records of medicine
-- ----------------------------
INSERT INTO `medicine` VALUES (8, '阿莫西林', '抗菌,消炎', '用于严重的猪肺炎、子宫炎，乳房炎及泌尿道感染', '辉瑞（Pfizer）', '与四环素等酸性药物及磺胺类药有配伍禁忌', '怀孕母猪慎用', '2~7毫克/千克体重，肌肉注射', 0, 'https://image.jianke.com/upload/prodimage/201606wm/2016620143552411.jpg', 10.50, '2025-02-25 23:48:51', '2025-02-25 23:48:51');
INSERT INTO `medicine` VALUES (9, '卡那霉素', '抗菌,呼吸道感染', '对呼吸道感染，特别对咳嗽、喘气较好', '鲁抗医药', '毒性较大', '怀孕母猪慎用', '5~15毫克/千克体重', 0, 'https://tse3-mm.cn.bing.net/th/id/OIP-C.8TS2meTgD1kvfHvHcLSZagHaHa?rs=1&pid=ImgDetMain', 8.75, '2025-02-25 23:48:51', '2025-02-25 23:48:51');
INSERT INTO `medicine` VALUES (10, '阿维菌素', '驱虫,抗菌', '新型驱虫药，对线虫、绦虫、吸虫及皮肤寄生虫疥癣均有较好效果', '拜耳（Bayer）', '毒性较大，易造成母猪流产', '怀孕母猪禁用', '2~7毫克/千克体重，肌肉注射', 0, 'https://cbu01.alicdn.com/img/ibank/O1CN01CdXnBX2EjILtJhiBw_!!2216490078780-0-cib.jpg?__r__=1699264009547', 12.00, '2025-02-25 23:48:51', '2025-02-25 23:48:51');
INSERT INTO `medicine` VALUES (11, '喹乙醇', '抗菌,促生长', '属于喹噁啉类，有抗菌、促生长作用', '新华制药', '抗菌药物', '无特殊禁忌', '50毫克/千克体重拌料', 0, 'https://image.jianke.com/upload/prodimage/201605wm/2016519151858698.jpg', 9.00, '2025-02-25 23:48:51', '2025-02-25 23:48:51');
INSERT INTO `medicine` VALUES (12, '安乃近', '解热镇痛', '起解热镇痛作用，临床上常用安乃近配合青霉素治疗一般性不吃料的猪病', '新华制药', '对怀孕母猪使用的剂量不能过大，否则会导致流产', '怀孕母猪慎用', '2~7毫克/千克体重，肌肉注射', 0, 'https://static.xgys.cn/uploads/20250120/17373702047495.jpg', 7.50, '2025-02-25 23:48:51', '2025-02-25 23:48:51');
INSERT INTO `medicine` VALUES (13, '链霉素', '抗菌,抗革兰氏阴性菌', '抗革兰氏阴性菌，在临床上常与青霉素配合使用', '新华制药', '与青霉素配合使用', '无特殊禁忌', '10毫克/千克体重', 0, 'https://tse1-mm.cn.bing.net/th/id/OIP-C.BHFz68KUlXgL5rYLcJ-Y3QHaE1?rs=1&pid=ImgDetMain', 6.00, '2025-02-25 23:48:51', '2025-02-25 23:48:51');
INSERT INTO `medicine` VALUES (14, '氨苄西林', '抗菌,消炎', '作为二线药，用于严重的肺炎，子宫炎，乳房炎及泌尿道感染', '哈药集团', '抗菌药物', '无特殊禁忌', '2~7毫克/千克体重，肌肉注射', 0, 'https://image.jianke.com/upload/prodimage/201605wm/2016519151858698.jpg', 9.25, '2025-02-25 23:48:51', '2025-02-25 23:48:51');
