-- ----------------------------
-- Table structure for news_articles
-- ----------------------------
DROP TABLE IF EXISTS `news_articles`;
CREATE TABLE `news_articles`  (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '新闻ID',
  `url` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '转载url',
  `title` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '新闻标题',
  `content` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '新闻内容',
  `author` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '作者',
  `publish_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '发布时间，默认为当前时间',
  `source` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '新闻来源',
  `summary` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '新闻摘要',
  `create_time` datetime NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 12 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '新闻资讯' ROW_FORMAT = DYNAMIC;

-- ----------------------------
-- Records of news_articles
-- ----------------------------
INSERT INTO `news_articles` VALUES (1, 'https://element-plus.org/zh-CN/component/icon.html', '全球首款猪肺炎多联疫苗在华获批上市 填补行业空白', '<p style=\"text-align: left;\">今日，国家兽药评审中心正式批准大北农集团与中国农业大学联合研发的 \"肺盾 3.0\" 猪肺炎多联疫苗上市。该疫苗采用重组蛋白表达技术，可同时防御猪肺炎支原体、副猪嗜血杆菌和胸膜肺炎放线杆菌三种病原体，解决了传统疫苗交叉保护不足的难题。<br class=\"container-eLPxqH wrapper-wt12I4 undefined\">\"肺盾 3.0\" 在全国 12 个省份的规模化猪场开展的临床实验显示，接种后 42 天内对三种病原体的综合保护率达 92%，较传统疫苗提升 27 个百分点。大北农生物疫苗事业部负责人表示，该疫苗采用冷链运输技术，可在 2-8℃环境下保存 18 个月，适合中国复杂养殖环境。<br class=\"container-eLPxqH wrapper-wt12I4 undefined\">中国农业大学动物医学院教授陈绍江指出：\"该疫苗的上市标志着我国在猪肺炎防控领域从跟随者向引领者的转变。通过精准靶向抗原设计，不仅降低了养殖成本，更减少了抗生素使用，符合国家绿色养殖政策导向。\"<br class=\"container-eLPxqH wrapper-wt12I4 undefined\">据了解，该疫苗已与温氏、牧原等头部养殖企业签订年度采购协议，预计 2025 年产能将突破 5 亿头份，覆盖全国 60% 的规模化猪场。</p>', '张科研', '2025-04-20 13:48:00', '农业科技日报', '<p>由大北农集团联合中国农业大学研发的全球首款猪肺炎多联疫苗 \"肺盾 3.0\" 今日获国家兽药评审中心批准上市。该疫苗通过基因编辑技术实现对 3 种血清型病原体的精准防御，临床试验显示保护率达 92%，预计年产能达 5 亿头份</p>', '2025-04-07 02:30:50', '2025-04-07 02:30:50');
INSERT INTO `news_articles` VALUES (3, 'http://eb.nkb.com.cn/', '姜堰区教育局创新打造智慧教学新范式 实现 \"学乐一体\"', '<p>姜堰区教育局今日宣布，其打造的智慧教育生态系统已覆盖全区 90% 的中小学，通过 \"技术 + 场景 + 评价\" 三维创新，推动教学模式变革。<br class=\"container-eLPxqH wrapper-wt12I4 undefined\">在姜堰中学，智慧课堂系统通过分析 2000 万条学生行为数据，为每位学生生成个性化学习图谱。高三学生李同学表示：\"系统推荐的错题本和拓展资源，让我的数学成绩提升了 23 分。\"<br class=\"container-eLPxqH wrapper-wt12I4 undefined\">姜堰四中的 AI 自习室配备智能答疑机器人和行为分析摄像头，学生自主学习效率提升 40%。校长张敏介绍：\"AI 系统能实时识别学生的困惑点，自动推送微视频讲解，同时通过表情识别技术提醒注意力不集中的学生。\"<br class=\"container-eLPxqH wrapper-wt12I4 undefined\">该系统还构建了 \"区 - 校 - 班 - 生\" 四级学情分析体系，投资 80 万元的区域大数据平台可实时监测各学科均衡度。教育局副局长李强表示：\"通过数据驱动，我们精准调整了 12 所薄弱学校的师资配置，校际成绩差异缩小了 18%。\"<br class=\"container-eLPxqH wrapper-wt12I4 undefined\">教育部基础教育司司长指出，姜堰模式为 \"双减\" 背景下的教学创新提供了可复制的样本，相关经验已纳入《全国智慧教育发展白皮书》。</p>', '王教育', '2025-04-07 10:42:20', '中国教育报', '<p>泰州市姜堰区教育局投资 450 万元打造智慧课堂教学系统，通过 AI 自习室、虚拟实验等技术重塑教学形态。姜堰中学大数据系统为学生定制学习路径，姜堰四中 AI 自习室提升自主学习效率，相关经验获教育部推广。</p>', '2025-04-07 02:42:43', '2025-04-07 02:42:43');
INSERT INTO `news_articles` VALUES (4, 'www.cenews.com.cn/news/2025041703', '万载生态环境局查处 145 吨工业污泥非法倾倒案 奖励举报人', '<p>万载生态环境局今日通报一起典型环境违法案件，某纸业公司因非法倾倒工业污泥被依法查处。执法人员通过无人机巡查和 DNA 溯源技术，锁定该公司在 5 月 4 日凌晨倾倒污泥的行为。<br class=\"container-eLPxqH wrapper-wt12I4 undefined\">经检测，倾倒的污泥含有重金属铬和镍，超出《危险废物鉴别标准》3 倍。该公司污水处理站负责人承认，为节省处置成本，将污泥运往废弃空地倾倒。<br class=\"container-eLPxqH wrapper-wt12I4 undefined\">\"我们通过 \' 天眼 \' 卫星遥感系统发现异常热成像，结合群众举报线索，24 小时内锁定了违法行为。\" 环境执法大队长刘刚介绍。<br class=\"container-eLPxqH wrapper-wt12I4 undefined\">根据《宜春市环境违法行为有奖举报实施办法》，举报人获得 5000 元奖励。生态环境局局长表示：\"该案是 \' 科技 + 共治 \' 的典型案例，我们将对举报人信息严格保密，鼓励更多群众参与环境监督。\"<br class=\"container-eLPxqH wrapper-wt12I4 undefined\">目前，涉事企业已完成污泥无害化处置，生态修复工程预计 6 月底完工。此案被生态环境部列为 2025 年长江经济带生态执法典型案例。</p>', '陈环保', '2025-04-20 15:21:42', '中国环境报', '<p>江西省万载县某纸业公司非法倾倒 145.67 吨工业污泥，生态环境局依据《固体废物污染环境防治法》对其处以 10 万元罚款，并对举报人兑现奖励。该案通过 \"群众举报 + 科技溯源\" 模式，彰显生态治理全民参与理念。</p>', '2025-04-20 07:22:13', '2025-04-20 07:22:13');
INSERT INTO `news_articles` VALUES (5, 'www.globaltimes.cn/news/2025041704', ' 中韩联合破获跨国仿冒媒体网络 涉 7 家虚假韩媒', '<p>中韩两国执法部门今日宣布，成功破获一起跨国仿冒媒体网络案件。韩国国情院发现中国 \"全媒社\" 公司运营的 7 个网站，冒用《首尔新闻》《济州日报》等韩国主流媒体名称，传播虚假信息。<br class=\"container-eLPxqH wrapper-wt12I4 undefined\">经调查，这些网站通过 AI 生成技术，每日发布 200 余条新闻，内容涉及中韩经贸、文化等领域。其中 30% 的新闻存在数据造假或断章取义情况。韩国国情院表示：\"这些仿冒网站试图通过 \' 伪权威 \' 形式影响舆论，已对 3 家韩国企业的股价造成波动。\"<br class=\"container-eLPxqH wrapper-wt12I4 undefined\">中国网信办网络安全协调局负责人指出：\"我们与韩方建立跨境数据取证通道，通过 IP 溯源和服务器镜像分析，72 小时内锁定了犯罪团伙。\" 目前，涉案的 12 名嫌疑人已被刑事拘留。<br class=\"container-eLPxqH wrapper-wt12I4 undefined\">此次行动是《中韩网络安全合作备忘录》签署后的首次联合执法。双方表示将建立常态化协作机制，每季度召开网络安全会议，共同打击跨境虚假信息传播。</p>', '刘国际', '2025-04-17 15:22:13', '环球时报', '<p>韩国国情院与中国网信办联合行动，捣毁中国 \"全媒社\" 运营的 7 个仿冒韩媒网站。这些网站盗用《首尔新闻》等名称，通过虚假新闻干扰舆论。中韩建立跨境网络安全协作机制，加强信息共享与执法合作。</p>', '2025-04-20 07:22:48', '2025-04-20 07:22:48');
INSERT INTO `news_articles` VALUES (6, 'www.healthychina.gov.cn/news/2025041805', '淮南市创新 \"加减乘除\" 工作法 书写爱国卫生新篇章', '<p>淮南市卫健委今日公布爱国卫生月活动成果，通过创新 \"加减乘除\" 工作法，构建起 \"政府主导、社会协同、公众参与\" 的健康治理新格局。<br class=\"container-eLPxqH wrapper-wt12I4 undefined\">\"加\" 强宣传方面，全市举办健康集市 56 场，发放健康工具包 10 万份，组织学生创作爱国卫生主题手抄报 3 万份。淮师附小通过显微镜观察细菌的实践课程，让学生直观感受卫生习惯的重要性。<br class=\"container-eLPxqH wrapper-wt12I4 undefined\">\"减\" 少陋习方面，医疗机构开展义诊 120 场次，为 5 万名市民提供体重管理、睡眠健康咨询。凤台县中学通过 \"洗手小能手\" 活动，使学生正确洗手率从 62% 提升至 91%。<br class=\"container-eLPxqH wrapper-wt12I4 undefined\">\"乘\" 势需求方面，建立 \"健康课堂进校园\" 机制，邀请安徽理工大学专家开展心理健康讲座 23 场。北师大淮南实验学校开设 \"心晴小屋\"，为学生提供个性化心理辅导。<br class=\"container-eLPxqH wrapper-wt12I4 undefined\">\"除\" 去病媒方面，全市投放灭鼠毒饵 20 吨，清理卫生死角 8000 余处。市疾控中心制作的 \"春季灭鼠科普漫画\" 在微信端阅读量突破 50 万次，有效提升市民参与度。<br class=\"container-eLPxqH wrapper-wt12I4 undefined\">\"通过这套组合拳，我们将爱国卫生运动从 \' 突击式 \' 转变为 \' 常态化 \'。\" 淮南市副市长表示，下一步将把经验推广至农村地区，打造 \"15 分钟健康生活圈\"。</p>', '李健康', '2025-04-20 15:22:50', '健康中国', '<p>淮南市在第 37 个爱国卫生月期间，通过 \"加减乘除\" 工作法推进健康城市建设。\"加\" 强宣传、\"减\" 少陋习、\"乘\" 势需求、\"除\" 去病媒，开展健康集市、心理辅导、春季灭鼠等活动，惠及群众超百万人次。</p>', '2025-04-20 07:23:20', '2025-04-20 07:23:20');
INSERT INTO `news_articles` VALUES (7, 'http://nynct.jiangsu.gov.cn/art/2022/7/29/art_13251_10557075.html', '牧原智能养殖机器人实现全流程无人化管理', '<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\">在牧原铜山养殖场，轨道巡检机器人通过红外热成像和声音识别技术，实时监测每头猪的健康状态。当系统发现某头猪体温异常时，会自动触发消毒机器人进行针对性处理。</div>\n<p>&nbsp;</p>\n<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\">板下清粪机器人采用闭式管链输送系统，可在 30 分钟内完成整个猪舍的粪便清理，用水量较传统方式减少 70%。消毒机器人搭载高温雾化设备，能在 1 小时内完成全场无死角消毒。</div>\n<p>&nbsp;</p>\n<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\">牧原技术总监介绍：「我们开发的智能养殖中枢系统，可整合 2000 余个传感器数据，实现猪群健康趋势预测和设备协同作业。目前，该系统已在全国 50 个养殖场部署。」</div>\n<p>&nbsp;</p>\n<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\">农业农村部专家指出，牧原模式推动我国生猪养殖从「经验管理」向「数据驱动」转型，预计 2025 年将带动行业劳动生产率提升 35%。</div>', '张创新', '2022-07-29 00:00:00', '江苏省农业农村厅', '<p>牧原股份自主研发的智能养殖机器人系统在江苏铜山养殖场投入使用，涵盖巡检、清粪、消毒等 10 余类机器人，实现万头猪场仅需 80 人管理，人工成本降低 40%，饲料浪费减少 15%。</p>', '2025-04-20 07:55:24', '2025-04-20 07:55:24');
INSERT INTO `news_articles` VALUES (8, 'https://m.zyzhan.com/tech_news/detail/268836.html', '非洲猪瘟唾液检测技术获国际认证 提前 5 天预警疫情', '<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\">该检测技术基于荧光定量 PCR 原理，通过采集猪的唾液样本实现早期筛查。在菲律宾某猪场的实测中，对低载量样本的检出率达 99.7%，而传统 PCR 方法仅为 85%。</div>\n<p>&nbsp;</p>\n<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\">凌越生医创始人陈作范介绍：「唾液检测可在感染后 3 天内检出病毒，比血液检测提前 5 天预警。操作简便，无需专业设备，特别适合中小型养殖场。」</div>\n<p>&nbsp;</p>\n<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\">农业农村部数据显示，该试剂已纳入政府采购目录，预计年产能达 1 亿份。世界动物卫生组织专家指出：「中国在 ASF 检测技术上的突破，为全球疫病防控提供了重要参考。」</div>', '陈防疫', '2025-03-03 00:00:00', '中国制药网', '<p style=\"text-align: left;\">凌越生医研发的 Sentinel 非洲猪瘟唾液检测试剂获世界动物卫生组织（WOAH）认证，成为全球首个获批的抗体快速检测产品。该试剂可在 10 分钟内完成检测，灵敏度较传统 ELISA 提升 10 倍，已在马来西亚、越南等国推广。</p>', '2025-04-20 07:56:24', '2025-04-20 07:56:24');
INSERT INTO `news_articles` VALUES (9, 'http://news.foodmate.net/2025/04/wap_713832.html', 'PCV2 新型纳米疫苗研究取得突破 免疫应答提升 3 倍', '<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\">该研究利用 MOFs 纳米材料包裹 PCV2 Cap 蛋白，构建了兼具抗原缓释和免疫增强功能的纳米疫苗。在小鼠实验中，接种该疫苗的动物产生的中和抗体水平是传统疫苗的 3 倍，CD8+T 淋巴细胞和 CD4+T 细胞增殖率提升 40%。</div>\n<p>&nbsp;</p>\n<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\">山东省农科院研究员介绍：「我们通过优化 MOFs 孔径和表面电荷，实现了抗原的精准释放和免疫佐剂的协同作用。这种纳米疫苗在 4℃条件下可稳定保存 6 个月，适合规模化生产。」</div>\n<p>&nbsp;</p>\n<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\">世界动物卫生组织（OIE）专家评价：「中国在 PCV2 疫苗技术上的突破，为全球猪病防控提供了新的技术路径。」目前，该技术已进入中试阶段，预计 2026 年可实现商业化应用。</div>', '王科研', '2025-04-20 15:56:25', '食品资讯_食品伙伴网', '<p>山东省农业科学院畜牧兽医所生猪疫病防控创新团队在《Materials Today Bio》发表研究论文，利用金属有机框架（MOFs）平台设计出新型纳米疫苗递送系统（Cap@ZIF-8-CpG），显著提升猪圆环病毒 2 型（PCV2）疫苗的免疫应答效果，抗体滴度提高 3 倍。</p>', '2025-04-20 07:57:00', '2025-04-20 07:57:00');
INSERT INTO `news_articles` VALUES (10, 'https://nyncw.cq.gov.cn/zwxx_161/zwdt/202504/t20250416_14526905_wap.html', '重庆 \"生猪产业大脑 2.0\" 开启 AI 养猪新时代 单头成本降 200 元', '<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\">在重庆荣昌区的 \"未来猪场\"，智能摄像头实时监测每头猪的体温、采食和运动数据。当系统发现某头猪连续 3 小时采食异常时，会自动推送健康预警，并通过 AI 算法诊断可能的疾病类型。养殖场负责人表示：「AI 系统能提前 48 小时预警腹泻风险，使治疗成功率提升 70%。」</div>\n<p>&nbsp;</p>\n<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\">该系统搭载的 \"畜牧 AI 一体机\" 集成了 2000 余条养殖知识库，可提供 24 小时在线问答服务。养殖户通过手机端输入症状描述，AI 可快速生成诊断方案。目前，该系统已服务 18 万养殖户，完成 5000 余头荣昌猪的溯源认证。</div>\n<p>&nbsp;</p>\n<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\">农业农村部专家指出，重庆模式为中小养殖户提供了可复制的智能化转型路径，预计 2025 年将在全国 10 个省份推广。</div>', '李智慧', '2025-04-16 15:57:03', '重庆市农业农村委员会', '<p>重庆市农业农村委员会今日宣布，其打造的 \"生猪产业大脑 2.0\" 已覆盖全市 60% 的规模化猪场。通过 AI 大模型 PigGPT、区块链溯源和隐私计算技术，实现生猪健康预警、精准饲喂和疫病防控的全链条数字化管理，单头生猪养殖成本降低 100-200 元。</p>', '2025-04-20 07:57:40', '2025-04-20 07:57:40');
INSERT INTO `news_articles` VALUES (11, 'https://www.moa.gov.cn/govpublic/xmsyj/202503/t20250324_6472167.htm', '西班牙海博莱生物大药厂猪水肿病亚单位疫苗在华获批', '<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\">根据农业农村部第 890 号公告，海博莱生物大药厂的猪水肿病亚单位疫苗通过国家兽药评审中心审批。该疫苗通过基因工程技术表达大肠杆菌 F18ab 菌毛抗原，结合新型佐剂系统，实现精准免疫应答。</div>\n<p>&nbsp;</p>\n<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\">在山东、河南等 8 个省份的规模化猪场试验中，仔猪在 10-15 日龄首免后，42 天抗体阳性率达 89%，腹泻发生率降低 65%。海博莱中国区技术总监介绍：「疫苗采用单剂量注射方案，可与猪瘟、口蹄疫等疫苗联合使用，减少应激反应。」</div>\n<p>&nbsp;</p>\n<div class=\"auto-hide-last-sibling-br paragraph-qzbcQC paragraph-element br-paragraph-space\">农业农村部兽医局负责人指出，该疫苗的上市将有效防控仔猪水肿病在保育阶段的流行，预计每年可减少经济损失超 15 亿元。目前，疫苗已与牧原、温氏等企业签订年度采购协议，首批产能达 5000 万头份。</div>', '陈兽药', '2025-04-20 15:57:40', '农业农村部官网', '<p>农业农村部今日公告，西班牙海博莱生物大药厂生产的猪水肿病亚单位疫苗正式获得《进口兽药注册证书》。该疫苗采用重组蛋白表达技术，针对致病性大肠杆菌 F18ab 血清型，临床试验显示仔猪免疫保护率达 91%，较传统灭活疫苗提升 28 个百分点。</p>', '2025-04-20 07:58:18', '2025-04-20 07:58:18');

-- ----------------------------
-- Table structure for pageview
-- ----------------------------
DROP TABLE IF EXISTS `pageview`;
CREATE TABLE `pageview`  (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '主键id',
  `pageviews` int NULL DEFAULT NULL COMMENT '浏览量',
  `illness_id` int NULL DEFAULT NULL COMMENT '病的id',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 19 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '浏览量' ROW_FORMAT = DYNAMIC;

